mapper.select:
	SELECT FILENO, FILENAME
      FROM SUIT_ATTACH
     WHERE REQ_NO = #{req_no}

SELECT REQ_NO
  FROM SUIT_RENT
 WHERE REQ_NO = (
			 	 SELECT MAX(REQ_NO)
				 FROM SUIT_RENT
				 WHERE MEMBER_ID = #{member_id}
			    )
   AND MEMBER_ID = #{member_id}
   AND RENT_ENABLED = 'Y'

mapper.insert:
INSERT INTO SUIT_ATTACH ( FILENO, FILENAME, REQ_NO ) SELECT TO_CHAR(SYSDATE,'DD')||LPAD(SEQ_FILE_NO.NEXTVAL,4,0), A.* FROM (
SELECT #{item.fileName} AS FILENAME, #{item.req_no} AS REQ_NO FROM DUAL
) A

mapper.update:
UPDATE INTERVIEW_COMPANY
   SET IC_NAME = #{ic_name}, 
       IC_DATE = #{ic_date}, 
       IC_PN = #{ic_pn}, 
       IC_LOCATION = #{ic_location}
 WHERE REQ_NO = #{req_no}
